import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { List, ListItem, ListItemText, Button, IconButton } from "@mui/material";
import { Delete } from "@mui/icons-material";
import axios from "../api/axiosInstance.ts";

interface Store {
    id: string;
    name: string;
}

const Stores: React.FC = () => {
    const [stores, setStores] = useState<Store[]>([]);

    useEffect(() => {
        const fetchStores = async () => {
            try {
                const response = await axios.get<Store[]>("/stores");
                setStores(response.data);
            } catch (error) {
                console.error("Error fetching stores:", error);
            }
        };
        fetchStores();
    }, []);

    const handleDelete = async (storeId: string) => {
        try {
            await axios.delete(`/stores/${storeId}`);
            setStores(stores.filter(store => store.id !== storeId));
        } catch (error) {
            console.error("Error deleting store:", error);
        }
    };

    return (
        <div>
            <List>
                {stores.map((store) => (
                    <ListItem key={store.id}>
                        <ListItemText primary={store.name} />
                        <IconButton onClick={() => handleDelete(store.id)} color="secondary">
                            <Delete />
                        </IconButton>
                        <Button component={Link} to={`/stores/${store.id}`} variant="outlined" color="primary">
                            View
                        </Button>
                        <Button component={Link} to={`/stores/${store.id}/edit`} variant="contained" color="primary">
                            Edit
                        </Button>
                    </ListItem>
                ))}
            </List>
            <Button variant="contained" color="primary" component={Link} to="/stores/new">
                Add Store
            </Button>
        </div>
    );
};

export default Stores;
